import java.awt.*;import BreezyGUI.*;public class TheGUIPart2 extends GBFrame{	private Label in; 	private Label pass;	private Label firstname;	private Label lastname;	private Label letters;	private Label upper;	private Label lower;			private TextField first; 	private TextField last; 	private TextField initals; 	private IntegerField lett; 	private TextField up; 	private TextField low; 		private Button findI;	private Button findL;	private Button changeU;	private Button changeL;   TheGUIPart2 ()   	{	   	firstname = addLabel ("First Name",1,1,1,1);  		lastname = addLabel ("Last Name",2,1,1,1);  		in = addLabel ("Initals are",5,1,1,1);  		letters = addLabel ("# of Letters",6,1,1,1);  		upper = addLabel (" Upper Case",7,1,1,1);  		lower = addLabel ("LowerCase",8,1,1,1);  				first = addTextField ("",1,2,1,1);		last = addTextField ("",2,2,1,1);		initals = addTextField ("",5,2,1,1);		lett = addIntegerField (0, 6,2,1,1);		up = addTextField ("",7,2,1,1);		low = addTextField ("",8,2,1,1);				findI = addButton ("Find Initals",3,1,1,1);		findL = addButton ("Letter Count",3,2,1,1);		changeU = addButton ("All UpperCase",4,1,1,1);		changeL = addButton ("All Lowercase",4,2,1,1);   	}   	public void buttonClicked (Button buttonObj){		String firstInput = first.getText();		String lastInput = last.getText();		   	if (buttonObj == findI){		char firstIni = firstInput.charAt(0);		char lastIni = lastInput.charAt(0);		initals.setText(firstIni +""+ lastIni);	}	else if (buttonObj == findL){		int letterCountF = firstInput.length();		int letterCountL = lastInput.length();		lett.setNumber(letterCountF + letterCountL);	}	else if (buttonObj == changeU){		up.setText(firstInput.toUpperCase()+ " "+ lastInput.toUpperCase() + "\n" );			}	else if (buttonObj == changeL){		low.setText(firstInput.toLowerCase()+ " "+ lastInput.toLowerCase() + "\n" );	}   	}	}